import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Button,
  Container,
  Typography,
  TextField,
} from '@mui/material'; // Importa i componenti Material-UI necessari

const GameDetail = ({ match }) => {
  const [game, setGame] = useState(null);
  const [rating, setRating] = useState(0);

  useEffect(() => {
    axios
      .get(`${backendUrl}/api/games/${match.params.id}`)
      .then((response) => {
        setGame(response.data);
      })
      .catch((error) => {
        console.error('Errore durante la richiesta API:', error);
      });
  }, [match.params.id]);

  const submitRating = () => {
    const requestData = {
      vote: parseInt(rating),
    };

    axios
      .post(`${backendUrl}/api/games/${game.id}/vote`, requestData)
      .then((response) => {
        setGame(response.data);
        console.log('Voto inviato con successo!');
      })
      .catch((error) => {
        console.error('Errore durante l\'invio del voto:', error);
      });
  };

  if (!game) {
    return <div>Caricamento...</div>;
  }

  return (
    <Container maxWidth="sm">
      <Typography variant="h4">{game.title}</Typography>
      <Typography variant="body1">{game.description}</Typography>
      <img
        src={`/images/${game.coverImage}`}
        alt={game.title}
        style={{ width: '100%', marginTop: '1rem', marginBottom: '1rem' }}
      />

      <Typography variant="h5">Dettagli Voti:</Typography>
      <Typography variant="body1">Rating: {game.rating}</Typography>
      <Typography variant="body1">Total Votes: {game.totalVotes}</Typography>

      <TextField
        type="number"
        inputProps={{
          min: 1,
          max: 5,
        }}
        value={rating}
        onChange={(e) => setRating(e.target.value)}
      />
      <Button onClick={submitRating} variant="contained" color="primary">
        Invia Voto
      </Button>
    </Container>
  );
};

export default GameDetail;
