import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Container, Typography, Card, CardContent, CardMedia, Button } from '@mui/material';

const backendUrl = 'http://localhost:8080'; // Modifica questa riga con l'URL del tuo backend

const GameList = () => {
  const [games, setGames] = useState([]);

  useEffect(() => {
    axios
      .get(`${backendUrl}/api/games`)
      .then((response) => {
        setGames(response.data);
      })
      .catch((error) => {
        console.error('Errore durante la richiesta API:', error);
      });
  }, []);

  const handleVote = (gameId, vote) => {
    const requestData = {
      vote: parseInt(vote),
    };

    axios
      .post(`${backendUrl}/api/games/${gameId}/vote`, requestData)
      .then((response) => {
        console.log('Voto aggiunto con successo:', response.data);
        // Effettua un aggiornamento della pagina dopo l'aggiunta del voto
        window.location.reload();
      })
      .catch((error) => {
        console.error('Errore durante la richiesta API:', error);
      });
  };

  if (games.length === 0) {
    return (
      <Container maxWidth="sm">
        <Typography variant="h4">Caricamento...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="sm">
      <Typography variant="h4">Elenco dei giochi</Typography>
      {/* Aggiungi il link al pannello di amministrazione */}
      <Link to="/admin" style={{ textDecoration: 'none' }}>
        <Button variant="contained" color="primary" style={{ marginBottom: '1rem' }}>
          Pannello di amministrazione
        </Button>
      </Link>
      {games.map((game) => (
        <div key={game.id} style={{ marginBottom: '1rem' }}>
          <Link to={`/games/${game.id}`} style={{ textDecoration: 'none' }}>
            <Card>
              <CardMedia
                component="img"
                image={`/images/${game.coverImage}`}
                alt={game.title}
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <CardContent>
                <Typography variant="h5">{game.title}</Typography>
                <Typography variant="body1">{game.description}</Typography>
                {/* Aggiungi le informazioni di voti e rating */}
                <Typography variant="body2">Voti totali: {game.totalVotes}</Typography>
                <Typography variant="body2">Media voto: {game.rating}</Typography>
              </CardContent>
            </Card>
          </Link>
          <div style={{ marginTop: '1rem' }}>
            <Typography variant="body2">Vota:</Typography>
            <Button variant="contained" color="primary" onClick={() => handleVote(game.id, 1)}>
              1
            </Button>
            <Button variant="contained" color="primary" onClick={() => handleVote(game.id, 2)}>
              2
            </Button>
            <Button variant="contained" color="primary" onClick={() => handleVote(game.id, 3)}>
              3
            </Button>
            <Button variant="contained" color="primary" onClick={() => handleVote(game.id, 4)}>
              4
            </Button>
            <Button variant="contained" color="primary" onClick={() => handleVote(game.id, 5)}>
              5
            </Button>
          </div>
        </div>
      ))}
    </Container>
  );
};

export default GameList;
