import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Button, Container, TextField, Typography, Collapse } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useTheme } from '@mui/material/styles';
import './AdminPage.css';

const AdminPage = () => {
  const [games, setGames] = useState([]);
  const [editingGame, setEditingGame] = useState(null);
  const [showAddGameForm, setShowAddGameForm] = useState(false);
  const navigate = useNavigate();

  const theme = useTheme();

  useEffect(() => {
    axios
      .get('http://localhost:8080/api/games')
      .then((response) => {
        setGames(response.data);
      })
      .catch((error) => {
        console.error('Errore durante la richiesta API:', error);
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post('http://localhost:8080/api/games', {
        title: editingGame ? editingGame.title : '',
        description: editingGame ? editingGame.description : '',
        coverImage: editingGame ? editingGame.coverImage : '',
      })
      .then((response) => {
        console.log('Gioco aggiunto con successo:', response.data);
        setEditingGame(null);
        window.location.reload();
      })
      .catch((error) => {
        console.error('Errore durante la richiesta API:', error);
      });
  };

  const handleDelete = (gameId) => {
    axios
      .delete(`http://localhost:8080/api/games/${gameId}`)
      .then((response) => {
        console.log('Gioco eliminato con successo:', gameId);
        window.location.reload();
      })
      .catch((error) => {
        console.error('Errore durante la richiesta API:', error);
      });
  };

  const handleEdit = (game) => {
    setEditingGame(game);
  };

  const saveEditedGame = () => {
    axios
      .put(`http://localhost:8080/api/games/${editingGame.id}`, {
        title: editingGame.title,
        description: editingGame.description,
        coverImage: editingGame.coverImage,
      })
      .then((response) => {
        console.log('Gioco modificato con successo:', response.data);
        setEditingGame(null);
        window.location.reload();
      })
      .catch((error) => {
        console.error('Errore durante la richiesta API:', error);
      });
  };

  const handleAddNewGame = () => {
    setEditingGame(null);
    setShowAddGameForm(!showAddGameForm);
  };

  const handleCancelEdit = () => {
    setEditingGame(null);
  };

  const editButtonStyle = {
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.getContrastText(theme.palette.primary.main),
    marginLeft: '1rem',
  };

  return (
    <ThemeProvider theme={createTheme(theme)}>
      <Container maxWidth="sm">
        <Typography variant="h4">Pannello di amministrazione</Typography>
        <Button color="primary" onClick={handleAddNewGame}>
          {showAddGameForm ? 'Nascondi Form' : 'Mostra Form di Inserimento'}
        </Button>

        <Collapse in={showAddGameForm}>
          <form onSubmit={handleSubmit}>
            <TextField
              fullWidth
              label="Titolo"
              value={editingGame ? editingGame.title : ''}
              onChange={(e) => setEditingGame({ ...editingGame, title: e.target.value })}
            />
            <TextField
              fullWidth
              label="Descrizione"
              multiline
              rows={4}
              value={editingGame ? editingGame.description : ''}
              onChange={(e) => setEditingGame({ ...editingGame, description: e.target.value })}
            />
            <TextField
              fullWidth
              label="URL Immagine di Copertina"
              value={editingGame ? editingGame.coverImage : ''}
              onChange={(e) => setEditingGame({ ...editingGame, coverImage: e.target.value })}
            />
            <Button type="submit" variant="contained" color="primary">
              {editingGame ? 'Salva Modifiche' : 'Aggiungi Gioco'}
            </Button>
            {editingGame && (
              <Button onClick={handleCancelEdit}>Annulla Modifiche</Button>
            )}
          </form>
        </Collapse>

        <Typography variant="h5">Elenco dei giochi</Typography>
        <table className="admin-game-table">
          <thead>
            <tr>
              <th>Titolo</th>
              <th>Voti totali</th>
              <th>Media voto</th>
              <th>Modifica</th>
              <th>Elimina</th>
            </tr>
          </thead>
          <tbody>
            {games.map((game) => (
              <React.Fragment key={game.id}>
                <tr>
                  <td>{game.title}</td>
                  <td>{game.totalVotes}</td>
                  <td>{game.rating.toFixed(2)}</td>
                  <td>
                    {editingGame?.id === game.id ? (
                      <div>
                        <Button className="edit-button" onClick={saveEditedGame}>
                          Salva
                        </Button>
                        <Button className="edit-button" onClick={handleCancelEdit}>
                          Annulla
                        </Button>
                      </div>
                    ) : (
                      <Button className="edit-button" onClick={() => handleEdit(game)}>
                        Modifica
                      </Button>
                    )}
                  </td>
                  <td>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => handleDelete(game.id)}
                    >
                      Elimina
                    </Button>
                  </td>
                </tr>
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </Container>
    </ThemeProvider>
  );
};

export default AdminPage;
